sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }
	var actual= g_form.getValue('actual_date');
	var tr=new GlideAjax('LmsAjax');
	tr.addParam('sysparm_name','getDateDiff');
	tr.addParam('sysparm_actual',actual);
	tr.addParam('sysparm_extend',newValue);
	tr.getXML(getDuration);
	function getDuration(response)
	{
		var result= response.responseXML.documentElement.getAttribute("result");
		var day=(result/(1000*60*60*24));
		var day1=day+1;
		g_form.setValue('duration',day1);
	}


   //Type appropriate comment here, and begin script below

}
