sys_script_include.script:
var askApproval = Class.create();
askApproval.prototype = {
    initialize: function() {
	},
compare:function(sysID){
	var result=[];
	var gr = new GlideRecord('x_574429_library_m_request_table');
	gr.addQuery('sys_id',sysID);
	gr.query();
	while(gr.next())
		{
			var object={};
			object.startDate = gr.u_glide_date_2;
			object.endDate = gr.u_glide_date_1;
			if(object.startDate <= object.endDate)
				{
					object.status= "Approved";
				}
			else
				{
					object.status="Rejected";
				}
			result.push(object);
		}
	return result;
},


    type: 'askApproval'
};
