sys_script_include.script:
var gettingData = Class.create();
gettingData.prototype = {
    initialize: function() {
    },
	gettingData:function(book_name){
	var result = [];
	var gr = new GlideRecord('x_574429_library_m_request_table');
	gr.addQuery('book_name',book_name);
	gr.query();
	while(gr.next())
		{
			var object = {};
			object.number=gr.number.toString();
			object.customer_id = gr.customer_id.toString();
			object.customer_name = gr.customer_name.toString();
			object.book_no = gr.book_no.toString();
			object.email_id = gr.email_id.toString();
			object.actual_date = gr.u_glide_date_2.toString();
			object.extending_date = gr.u_glide_date_1.toString();
			result.push(object);
		}

		return result;
},




    type: 'gettingData'
};
