sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

    // implement resource here
	var object={};
	var inc =[];
	var requestBody = request.body;
	var requestData = requestBody.data;
	//var number=requestBody.data.number.toString();
	try{
			var gr=new GlideRecord('x_574429_library_m_request_table');
			//var recordNumber=gr.number;
		    gr.addQuery('number',gr.number);
			gr.query();
			gr.book_name.setDisplayValue(requestData.book_name);
			gr.customer_name.setDisplayValue(requestData.customer_name);
			gr.customer_id.setDisplayValue(requestData.customer_id);
			gr.insert();

			//gr.addQuery('number',gr.number);
			//gr.query();
			if(gr.next())
				{
					inc.push({
						'Number':gr.number,
						'Customer Id':gr.customer_id,
						'Customer Name':gr.customer_name,
						'Book Name':gr.book_name,
						'Email Id':gr.email_id.getDisplayValue()  
					});
				}

	}catch(e){
		object.message=e.message;    
	}
	response.setBody(inc);

})(request, response);
